import React from 'react';
import './comments.css'
import Comment from './comment/comment';

function Comments({hidden, comments}) {
    if(hidden){
        return;
    }
    return (
        <div className='comments' >
            {comments.map((comment, index) => {
                <Comment />
            })}
        </div>
    );
  }

  export default Comments;
