import React from 'react';
import Head from './components/head/head';
import Posts from './components/posts/posts';
import './App.css'
import Categories from './components/categories/categories';
import { useDispatch } from "react-redux";
import { memes as memesSlice } from './app/memesSlice';

function App() {

  const dispatch = useDispatch()
  const fetchMemes = () =>{
    let memes =[];
    fetch('https://www.reddit.com/r/memes.json')
    .then(response => response.json())
    .then(body => {
      for(let i=0; i< body.data.children.length; i++){
        if(body.data.children[i].data.post_hint === 'image'){
          let img = {
            img: body.data.children[i].data.url_overridden_by_dest,
            text: body.data.children[i].data.title,
            author: body.data.children[i].data.author_fullname
          }
          memes.push(img);
        }
      }
      return Promise.all;
    })
  }
  dispatch(memesSlice.actions.addMemes(fetchMemes()));

  return (
    <div>
      <div className={'head'} >
        <Head />
      </div>
      <div className={'main'} >
        <Posts />
        <Categories />
      </div>
    </div>
  );
}

export default App;
