import React from 'react';
import './like.css'
import up from '../../../../upVote.png'
import down from '../../../../downVote.png'

function Like({id}) {

  const upVote = (id, likeColor) => {
    const num = document.getElementById('like'+id);
    if(num.style.color !== 'green'){
      num.style.color = 'green';
    }else{
      if(document.body.style.backgroundColor !== 'rgb(255, 255, 255)'){
        num.style.color = 'black';

      }else{
        num.style.color = 'black';
      }
    }
  }

  const handleUpVote = (id) =>{
    return upVote(id)
  }

  const like = Math.round(Math.random()* 1000)


    return (
      <div className={'like' }>
          <img src={up} alt={'up vote'} className={'vote'} onClick={handleUpVote(id)}></img>
            <p style={{color: likeColor}}>{like}</p>
          <img src={down} alt={'down vote'} className={'vote'}></img>
      </div>
    );
  }

  export default Like;
