import { createSlice } from "@reduxjs/toolkit";


export const memes = createSlice({
    name: 'memes',
    initialState: [],
    reducers: {
        addMemes: (state, action) => {
            state = action.payload;
        }
    }
});

export const selectMemes = (state) => {
    return state.memes.memes;
}
