import { createSlice } from "@reduxjs/toolkit";


export const memes = createSlice({
    name: 'memes',
    initialState: [],
    reducers: {
        addMemes: (state, action) => {
            return [...action.payload]
        }, 
        setLikeColor: (state, action) => {
            return state.map(meme => {
                if(meme.ID === action.payload.id){
                    meme.like = action.payload.color;
                }
                return meme;
            })
        },
        toggle: (state, action) => {
            return state.map(meme => {
                if(meme.ID === action.payload.id){
                    meme.like = action.payload.color;
                }
                return meme;
            })
        }

    }
});

export const selectMemes = (state) => {
    return state.memes;
}
