import React from 'react';
import './post.css'
import Like from './like/like';
import Text from './text/text';
import comment from '../../../comment.png'
import Media from './media/media';
import Comments from './comments/comments';
import { memes } from '../../../app/memesSlice';
import { useDispatch } from 'react-redux';

function Post({au, text, img, id, likeColor, showComment, likes, permalink, comments}) {

  const dispatch = useDispatch();

  const getComments = (permalink) => {
    return fetch(`https://www.reddit.com${permalink}.json`)
    .then(response => response.json())
    .then(body => {
      const comments = [];
      for(let i =0; i<body[1].data.children.length; i++){
        const first = body[1].data.children[i].data.body_html.substr(31)
        const index = first.indexOf('&')
        const cmmnt = first.substr(0, first.length-23)
        const comment = {
          author: body[1].data.children[i].data.author,
          text: cmmnt
        }
        if(comment.text.includes())
        comments.push(comment);
      }

      return Promise.all(comments);
    })
  }

  function toggleComment(e){
    const id = e.target.getAttribute('value');
    let hide = true;
    if(showComment === true){
      hide = false;
    }
    //console.log(e.target.getAttribute('value'))
    if(comments.length <=0){
      getComments(permalink).then( comments => {
        dispatch(memes.actions.addComment({id: id, comments: comments}))
      }
      )
    }
    dispatch(memes.actions.toggleComment({id: id, comment: hide}));
  }

    return (
      <div className={'post-container' }>
          <div className={'like-text' }>
            <Like id={id} likeColor={likeColor} likes={likes}/> 
            <Text text={text} au={au}/>
          </div>
          <Media src={img}/>
          <div className='right-align'>
              <img src={comment} alt={'comment'} className={'comment-icon'} value={id} onClick={e => toggleComment(e)}></img>
          </div>
          <Comments hidden={showComment} comments={comments}/>
      </div>
    );
  }

  export default Post;
