import React from 'react';
import './like.css'
import up from '../../../../upVote.png'
import down from '../../../../downVote.png'
import { memes } from '../../../../app/memesSlice';
import { useDispatch} from 'react-redux';

function Like({id, likeColor}) {

  const dispatch = useDispatch();

  const upVote = (id) => {
    if(likeColor !== 'green'){
      dispatch(memes.actions.setLikeColor({id: id, color: 'green'}));
    }else{
      if(document.body.style.backgroundColor !== 'rgb(255, 255, 255)'){
        dispatch(memes.actions.setLikeColor({id: id, color: 'white'}));

      }else{
        dispatch(memes.actions.setLikeColor({id: id, color: 'black'}));
      }
    }
  }

  const handleUpVote = (id) =>{
    return upVote(id)
  }

  const like = Math.round(Math.random()* 1000)

    return (
      <div className={'like' }>
          <img src={up} alt={'up vote'} className={'vote'} onClick={handleUpVote()}></img>
            <p style={{color: likeColor}}>{like}</p>
          <img src={down} alt={'down vote'} className={'vote'}></img>
      </div>
    );
  }

  export default Like;
