import React from 'react';
import './post.css'
import Like from './like/like';
import Text from './text/text';
import comment from '../../../comment.png'
import Media from './media/media';
import Comments from './comments/comments';
import { memes } from '../../../app/memesSlice';
import { useDispatch } from 'react-redux';

function Post({au, text, img, id, like, showComment}) {

  const dispatch = useDispatch();

  function toggleComment(id){
    let hide = true;
    if(showComment === true){
      hide = false;
    }

    dispatch(memes.actions.toggleComment({id: id, comment: hide}));
  }

    return (
      <div className={'post-container' }>
          <div className={'like-text' }>
            <Like id={id} likeColor={like}/> 
            <Text text={text} au={au}/>
          </div>
          <Media src={img}/>
          <div className='right-align'>
            <button value={id} > 
              <img src={comment} alt={'comment'} className={'comment-icon'}></img>
            </button>
          </div>
          <Comments hidden={showComment}/>
      </div>
    );
  }

  export default Post;
