import React, { useState } from 'react';
import searchImg from './../../../search-259.svg'
import './searchBar.css'
import { search } from '../../../app/searchSlice';
import { useDispatch } from 'react-redux';

function SearchBar() {

  const dispatch = useDispatch();
  const [term, setTerm] = useState('');

  const onChange = (e) =>{
    setTerm(e.target.value);
  }

  const submitSearchTerm = () =>{
    dispatch(search.actions.setSearchTerm(term));
  }

  return (
    <form className={'search-container'} onSubmit={submitSearchTerm}>
        <input type={'text'} placeholder={'Search'} className={'search-bar'} onChange={(e) => onChange(e)}></input>
        <input type='submit' value={<img src={searchImg} className={'search-icon'} alt={'Submit'} />}>
          {/* <img src={searchImg} className={'search-icon'} alt={'Submit'} /> */}
        </input>
    </form>
  );
}

export default SearchBar;
